import pandas as pd
from sklearn.model_selection import train_test_split

def task_func(df, target_column, column_to_remove="c", test_size=0.2):
    df = pd.DataFrame(df)

    # Drop the specified column if it exists in the dataframe
    if column_to_remove in df.columns:
        df = df.drop(columns=column_to_remove)

    # Split the dataframe into training and test datasets
    try:
        X_train, X_test, y_train, y_test = train_test_split(
            df.drop(columns=target_column), df[target_column], test_size=test_size
        )
    except ValueError as e:
        raise ValueError(f"Invalid test_size parameter: {e}")

    return X_train, X_test, y_train, y_test

import numpy as np
import cv2
import os

def task_func(image_path='image.jpg', threshold=128):
    if not isinstance(threshold, int) or not (0 <= threshold <= 255):
        raise ValueError("Threshold must be an integer between 0 and 255.")

    if not os.path.exists(image_path):
        raise FileNotFoundError(f"No image found at {image_path}")

    img = cv2.imread(image_path, cv2.IMREAD_GRAYSCALE)
    if img is None:
        raise ValueError("Unable to read the image file. Please check the file format.")

    binary_img = np.where(img > threshold, 255, 0).astype('uint8')

    return img, binary_img

import pandas as pd
import matplotlib.pyplot as plt

def task_func(data):
    # Check if input is a list of dictionaries
    if not isinstance(data, list) or not all(isinstance(item, dict) for item in data):
        raise TypeError("Input must be a list of dictionaries.")

    # Check if all values in dictionaries are integers
    for item in data:
        for key, value in item.items():
            if not isinstance(value, (int, float)):
                raise TypeError("All values in dictionaries must be integers or floats.")

    df = pd.DataFrame(data)
    df.fillna(0, inplace=True)
    for fruit in df.columns:
        plt.plot(df[fruit], label=fruit)
    plt.xlabel("Time")
    plt.ylabel("Sales Quantity")
    plt.title("Fruit Sales over Time")
    plt.legend()
    return plt.gca()

from collections import defaultdict
import numpy as np
import matplotlib.pyplot as plt

def task_func(data):
    if not data:
        raise ValueError("Input data is empty.")
    if not isinstance(data, list) or not all(isinstance(d, dict) for d in data):
        raise TypeError("Input must be a list of dictionaries.")
    for d in data:
        if not all(isinstance(value, (int, float)) for value in d.values()):
            raise TypeError("All values in the dictionaries must be numeric.")

    stats = defaultdict(list)
    for d in data:
        for key, value in d.items():
            stats[key].append(value)

    result = {k: {"mean": np.mean(v), "std": np.std(v)} for k, v in stats.items()}

    # Visualization
    axes = []
